import React, { useEffect, useState, createContext } from "react";
import Popup from "./Popup/Popup";

const Portfolio = () => {
  const [state, setState] = useState();
  const [search, setSearch] = useState([]);
  const [update, setUpdate] = useState({
    unit: 1,
    schemecode: null,
    popup: false,
  });

  const scheme = createContext();

  useEffect(async () => {
    const mf = await fetch(`https://api.mfapi.in/mf/search?q=${state}`);
    const jsonmf = await mf.json();
    // console.log(mf);
    console.log(jsonmf);
    setSearch(jsonmf);
  }, [state]);

  const handleUserSearch = (e) => {
    setState(e.target.value);
  };

  return (
    <div className="main">
      <div className="navbar">
        <h1 className="heading">Mutual Fund Store</h1>
      </div>
      <div className="content_container">
        <div className="search_container">
          <input
            type="text"
            className="search"
            placeholder="Search"
            onChange={handleUserSearch}
            value={state}
          />
        </div>
        <div className="units_container">
          <p className="units">SBI</p>

          <p className="units">
            {search.map((value, index) => {
              console.log("Value", value.schemeCode);
              return (
                <scheme.Provider value={update.schemecode}>
                  <p>
                    <a
                      href="#"
                      onClick={() => {
                        setUpdate(() => {
                          return {
                            schemecode: value.schemeCode,
                            popup: true,
                          };
                        });
                        console.log(update.popup);
                        console.log(update.schemecode);
                        console.log(update.unit);
                      }}
                    >
                      {value.schemeName}
                    </a>
                  </p>
                  {update.popup ? (
                    <Popup
                      key={index}
                      ind={index}
                      schemecode={value.schemeCode}
                    />
                  ) : null}
                </scheme.Provider>
              );
            })}
          </p>
        </div>
        <div className="total_units_container">
          <p className="total_units">total_units</p>
        </div>
      </div>
      <div className="footer"></div>
    </div>
  );
};

export default Portfolio;
